(()=>{"use strict";(()=>{const e=()=>{const t=new Intl.DateTimeFormat("ru",{dayPeriod:"narrow"}).format(new Date),n=Math.floor((new Date("1 january "+(+(new Date).getFullYear()+1))-new Date)/1e3/60/60/24),a=document.getElementById("time-of-day"),o=document.getElementById("today"),m=document.getElementById("current-time"),r=document.getElementById("still-until-new-year");var l;a.textContent={утра:"Доброе утро",дня:"Добрый день",вечера:"Добрый вечер",ночи:"Доброй ночи"}[t],o.textContent=`Сегодня: ${l=new Intl.DateTimeFormat("ru",{weekday:"long"}).format(new Date),l?l[0].toUpperCase()+l.slice(1):l}`,m.textContent=`Текущее время: ${new Intl.DateTimeFormat("en",{timeStyle:"medium"}).format(new Date)}`,r.textContent=`До нового года осталось ${n} ${(e=>{const t=e%10;return 1===t?"день":t>1&&t<5?"дня":"дней"})(n)}`,setTimeout(e,1e3)};e()})()})();